sysauto_script.script:
//Get today's date and time
//create a variable for how far into the future we consider Due Soon
var rightNow = new GlideDateTime();

//Query database for need it tasks that are due within the next
//24 hours and are not in closed/complete state
var nitasksDueSoon = new GlideRecord('x_58872_needit_needit_task');

nitasksDueSoon.addQuery('due_date', '>=', rightNow);
nitasksDueSoon.addQuery('due_date', '<=', gs.hoursAgo(-24));
nitasksDueSoon.addQuery('state', '!=', 3);

nitasksDueSoon.query();

//Write a log for all query results
while(nitasksDueSoon.next()){
	gs.info("Need It Task due soon: " + nitasksDueSoon.number);
	gs.eventQueue("x_58872_needit.dueSoonNeedItTasks", nitasksDueSoon, nitasksDueSoon.number, gs.getUserName(), 'my_queue');
}
